query.statement:
SELECT <FIELDS/> 
FROM <TABLE/> 
WHERE <FIELD name='period'/> = ${period}
AND <FIELD name='domain'/> = ${domain}
AND <FIELD name='name'/> = ${name}

SELECT <FIELDS/> 
FROM <TABLE/> 
WHERE <FIELD name='period'/> = ${period}
AND <FIELD name='domain'/> = ${domain}
AND <FIELD name='type'/> = ${type}
AND <FIELD name='name'/> = ${name}

SELECT <FIELDS/> 
FROM <TABLE/> 
WHERE <FIELD name='period'/> = ${period}
AND <FIELD name='type'/> = ${type}
AND <FIELD name='name'/> = ${name}

SELECT <FIELDS/> 
FROM <TABLE/> 
WHERE <FIELD name='status'/> = ${status}
<IF type='NOT_NULL' field='consumer'>
	AND  <FIELD name='consumer'/> = ${consumer}
</IF>
